import React, { Component } from 'react';
import { Link } from 'react-router-dom';


export default class Navbar extends Component {
  state = {
    sideDrawerOpen: false
  };

 //react to a click on the toolbar
 drawerToggleClickHandler = () => {
  //prevState is passed into the function by React
  this.setState((prevState) => {
    return {sideDrawerOpen: !prevState.sideDrawerOpen};
  });
};

//this always will close the sidedrawer
backdropClickHandler = () => {
  this.setState({sideDrawerOpen: false});
};


  render() {
    let backdrop;

    if (this.state.sideDrawerOpen) {
      backdrop = <BackDrop click={this.backdropClickHandler} />
    }
    return (

 // 100% height for the whole app. This is import to give the side drawer a 100% height through inheritance
      //  You could use 100vh, but that is not supported in all browsers!!!! Or you use a 100% for the page and 100% for the child element
      //100% on index.css
      <div style={{ height: "100%" }}>
        <Toolbar drawerClickHandler={this.drawerToggleClickHandler} />
        {/* for you to do a transition effect with the sidebar, it should be on/true by default, else you can use css effect */}
        {/* the sidebar will always be present and you will choose if it will be shown or not */}
        <SideDrawer show={this.state.sideDrawerOpen} />

        {/* <BackDrop /> */}
        {backdrop}


        <main style={{ marginTop: "64px" }}>
          {/* You need to give the p a marginTop: '64px'. because it doesn't respect the toolbar */}
        </main>
      </div>

      // <nav className="navbar navbar-dark bg-dark navbar-expand-lg">
